INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
	(9, 9, 'Plaza Ajax Search', 'plaza_ajax_search', 'Plaza Themes', '1.0', '', '<modification>\r\n    <name>Plaza Ajax Search</name>\r\n	<version>1.0</version>\r\n	<author>Plaza Themes</author>\r\n	<code>plaza_ajax_search</code>\r\n\r\n	<file path="catalog/controller/common/header.php">\r\n		<operation>\r\n			<search ><![CDATA[$data[\'search\'] = $this->load->controller(\'common/search\');]]></search>\r\n			<add position="replace"><![CDATA[\r\n				$search_status = $this->config->get(\'module_ptsearch_status\');\r\n				if($search_status) {\r\n					$data[\'search\'] = $this->load->controller(\'extension/module/ptsearch\');\r\n					$data[\'search_status\'] = true;\r\n				} else {\r\n					$data[\'search\'] = $this->load->controller(\'common/search\');\r\n					$data[\'search_status\'] = false;\r\n				}\r\n			]]></add>\r\n		</operation>\r\n	</file>	\r\n</modification>', 1, '2019-01-09 19:15:33'),
	(44, 44, 'Plaza Control Panel', 'plaza_control_panel', 'Plaza Themes', '18.12.27', '', '<?xml version="1.0" encoding="utf-8"?>\r\n<modification>\r\n    <code>plaza_control_panel</code>\r\n    <name>Plaza Control Panel</name>\r\n    <version>18.12.27</version>\r\n    <author>Plaza Themes</author>\r\n\r\n    <!-- Menu Left in Admin -->\r\n    <file path="admin/controller/common/column_left.php">\r\n        <operation>\r\n            <search ><![CDATA[$catalog = array();]]></search>\r\n            <add position="before"><![CDATA[\r\n            $this->load->language(\'plaza/adminmenu\');\r\n\r\n            if($this->user->hasPermission(\'access\', \'extension/module/ptcontrolpanel\')) {\r\n                $data[\'menus\'][] = array(\r\n                    \'id\'       => \'plaza-dashboard\',\r\n                    \'icon\'     => \'fa-plaza\',\r\n                    \'name\'     => $this->language->get(\'text_theme_dashboard\'),\r\n                    \'href\'     => $this->url->link(\'extension/module/ptcontrolpanel\', \'user_token=\' . $this->session->data[\'user_token\'], true)\r\n                );\r\n            }\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <!-- PRODUCT CONFIGURATION -->\r\n    <file path="admin/controller/catalog/product.php"> <!-- Done -->\r\n        <operation>\r\n            <search ><![CDATA[public function index() {]]></search>\r\n            <add position="after"><![CDATA[\r\n        $this->load->model(\'plaza/controlpanel\');\r\n        $this->model_plaza_controlpanel->setupRotateImage();\r\n        $this->model_plaza_controlpanel->setupColorSwatches();\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search ><![CDATA[$this->load->language(\'catalog/product\');]]></search>\r\n            <add position="before"><![CDATA[\r\n                $this->load->language(\'plaza/rotateimage\');\r\n                $this->load->language(\'extension/module/ptcontrolpanel\');\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search ><![CDATA[\'sort_order\' => $product_image[\'sort_order\']]]></search>\r\n            <add position="before"><![CDATA[\r\n                \'is_rotate\' => $product_image[\'is_rotate\'],\r\n                \'product_option_value_id\' => $product_image[\'product_option_value_id\'],\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path="admin/model/catalog/product.php"> <!-- Done -->\r\n        <operation>\r\n            <search><![CDATA[$this->db->query("INSERT INTO " . DB_PREFIX . "product_image SET product_id = \'" . (int)$product_id . "\', image = \'" . $this->db->escape($product_image[\'image\']) . "\', sort_order = \'" . (int)$product_image[\'sort_order\'] . "\'");]]></search>\r\n            <add position="replace">\r\n                <![CDATA[\r\n                    if(isset($product_image[\'product_option_value_id\']) && $product_image[\'product_option_value_id\']) {\r\n                        $this->db->query("INSERT INTO " . DB_PREFIX . "product_image SET product_id = \'" . (int)$product_id . "\', image = \'" . $this->db->escape($product_image[\'image\']) . "\', sort_order = \'" . (int)$product_image[\'sort_order\'] . "\', is_rotate = \'" . (int)$product_image[\'is_rotate\'] . "\', product_option_value_id = \'" . (int)$product_image[\'product_option_value_id\'] . "\'");\r\n                    } else {\r\n                        $this->db->query("INSERT INTO " . DB_PREFIX . "product_image SET product_id = \'" . (int)$product_id . "\', image = \'" . $this->db->escape($product_image[\'image\']) . "\', sort_order = \'" . (int)$product_image[\'sort_order\'] . "\', is_rotate = \'" . (int)$product_image[\'is_rotate\'] . "\'");\r\n                    }\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n\r\n    \r\n\r\n    <file path="catalog/controller/product/product.php"> <!-- Done -->\r\n        <operation>\r\n            <search><![CDATA[$data[\'heading_title\'] = $product_info[\'name\'];]]></search>\r\n            <add position="before"><![CDATA[\r\n            /* Plaza Product Configuration */\r\n            $this->load->model(\'tool/image\');\r\n            $store_id = $this->config->get(\'config_store_id\');\r\n\r\n            /* Catalog Mode */\r\n            if(isset($this->config->get(\'module_ptcontrolpanel_product_price\')[$store_id])) {\r\n                $data[\'show_product_price\'] = (int) $this->config->get(\'module_ptcontrolpanel_product_price\')[$store_id];\r\n            } else {\r\n                $data[\'show_product_price\'] = 0;\r\n            }\r\n\r\n            if(isset($this->config->get(\'module_ptcontrolpanel_product_cart\')[$store_id])) {\r\n                $data[\'show_product_button_cart\'] = (int) $this->config->get(\'module_ptcontrolpanel_product_cart\')[$store_id];\r\n            } else {\r\n                $data[\'show_product_button_cart\'] = 0;\r\n            }\r\n\r\n            if(isset($this->config->get(\'module_ptcontrolpanel_product_wishlist\')[$store_id])) {\r\n                $data[\'show_product_button_wishlist\'] = (int) $this->config->get(\'module_ptcontrolpanel_product_wishlist\')[$store_id];\r\n            } else {\r\n                $data[\'show_product_button_wishlist\'] = 0;\r\n            }\r\n\r\n            if(isset($this->config->get(\'module_ptcontrolpanel_product_compare\')[$store_id])) {\r\n                $data[\'show_product_button_compare\'] = (int) $this->config->get(\'module_ptcontrolpanel_product_compare\')[$store_id];\r\n            } else {\r\n                $data[\'show_product_button_compare\'] = 0;\r\n            }\r\n\r\n            if(isset($this->config->get(\'module_ptcontrolpanel_product_options\')[$store_id])) {\r\n                $data[\'show_product_options\'] = (int) $this->config->get(\'module_ptcontrolpanel_product_options\')[$store_id];\r\n            } else {\r\n                $data[\'sho