%}\n          &lt;/ul&gt;\n        &lt;/div&gt;\n        {% endif %}\n        \n        &lt;div class=&quot;col-12 col-sm-6 col-md-4 col-lg-3&quot;&gt;\n          &lt;h2&gt;{{ text_my_orders }}&lt;/h2&gt;\n          &lt;ul class=&quot;list-unstyled&quot;&gt;\n            &lt;li&gt;&lt;a href=&quot;https://www.domex.lk/&quot; target=&quot;_blank&quot;&gt;Track your order&lt;/a&gt;&lt;/li&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ order }}&quot;&gt;{{ text_order }}&lt;/a&gt;&lt;/li&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ download }}&quot;&gt;{{ text_download }}&lt;/a&gt;&lt;/li&gt;\n            {% if reward %}\n            &lt;li&gt;&lt;a href=&quot;{{ reward }}&quot;&gt;{{ text_reward }}&lt;/a&gt;&lt;/li&gt;\n            {% endif %}\n            &lt;li&gt;&lt;a href=&quot;{{ return }}&quot;&gt;{{ text_return }}&lt;/a&gt;&lt;/li&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ transaction }}&quot;&gt;{{ text_transaction }}&lt;/a&gt;&lt;/li&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ recurring }}&quot;&gt;{{ text_recurring }}&lt;/a&gt;&lt;/li&gt;\n          &lt;/ul&gt;\n        &lt;/div&gt;\n        \n        &lt;div class=&quot;col-12 col-sm-6 col-md-4 col-lg-3&quot;&gt;\n          &lt;h2&gt;{{ text_my_affiliate }}&lt;/h2&gt;\n          &lt;ul class=&quot;list-unstyled&quot;&gt;\n            {% if not tracking %}\n            &lt;li&gt;&lt;a href=&quot;{{ affiliate }}&quot;&gt;{{ text_affiliate_add }}&lt;/a&gt;&lt;/li&gt;\n            {% else %}\n            &lt;li&gt;&lt;a href=&quot;{{ affiliate }}&quot;&gt;{{ text_affiliate_edit }}&lt;/a&gt;&lt;/li&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ tracking }}&quot;&gt;{{ text_tracking }}&lt;/a&gt;&lt;/li&gt;\n            {% endif %}\n          &lt;/ul&gt;\n        &lt;/div&gt;\n        \n        &lt;div class=&quot;col-12 col-sm-6 col-md-4 col-lg-3&quot;&gt;\n          &lt;h2&gt;{{ text_my_newsletter }}&lt;/h2&gt;\n          &lt;ul class=&quot;list-unstyled&quot;&gt;\n            &lt;li&gt;&lt;a href=&quot;{{ newsletter }}&quot;&gt;{{ text_newsletter }}&lt;/a&gt;&lt;/li&gt;\n          &lt;/ul&gt;\n        &lt;/div&gt;\n      &lt;/div&gt;\n      {{ content_bottom }}&lt;/div&gt;\n    {{ column_right }}&lt;/div&gt;\n&lt;/div&gt;\n{{ footer }}', '2023-11-27 11:01:43'),
	(39, 0, 'tt_rozer1', 'information/contact', '{{ header }}\n&lt;div id=&quot;information-contact&quot; class=&quot;container&quot;&gt;\n  &lt;ul class=&quot;breadcrumb&quot;&gt;\n    {% for breadcrumb in breadcrumbs %}\n    &lt;li&gt;&lt;a href=&quot;{{ breadcrumb.href }}&quot;&gt;{{ breadcrumb.text }}&lt;/a&gt;&lt;/li&gt;\n    {% endfor %}\n  &lt;/ul&gt;\n  &lt;div class=&quot;row&quot;&gt;{{ column_left }}\n    {% if column_left and column_right %}\n    {% set class = \'col-sm-12 col-md-8\' %}\n    {% elseif column_left or column_right %}\n    {% set class = \'col-sm-12 col-md-8\' %}\n    {% else %}\n    {% set class = \'col-sm-12 col-md-8\' %}\n    {% endif %}\n    &lt;div id=&quot;content&quot; class=&quot;{{ class }}&quot;&gt;{{ content_top }}\n      &lt;h1&gt;{{ heading_title }}&lt;/h1&gt;\n      &lt;h3&gt;{{ text_location }}&lt;/h3&gt;\n      &lt;div class=&quot;panel panel-default&quot;&gt;\n        &lt;div class=&quot;panel-body&quot;&gt;\n          &lt;div class=&quot;row&quot;&gt;\n            {% if image %}\n            &lt;div class=&quot;col-sm-3&quot;&gt;&lt;img src=&quot;{{ image }}&quot; alt=&quot;{{ store }}&quot; title=&quot;{{ store }}&quot; class=&quot;img-thumbnail&quot; /&gt;&lt;/div&gt;\n            {% endif %}\n            &lt;div class=&quot;col-sm-3&quot;&gt;&lt;strong&gt;{{ store }}&lt;/strong&gt;&lt;br /&gt;\n              &lt;address&gt;\n              {{ address }}\n              &lt;/address&gt;\n              &lt;!-- {% if geocode %}\n              &lt;a href=&quot;https://maps.google.com/maps?q={{ geocode|url_encode }}&amp;hl={{ geocode_hl }}&amp;t=m&amp;z=15&quot; target=&quot;_blank&quot; class=&quot;btn btn-info&quot;&gt;&lt;i class=&quot;fa fa-map-marker&quot;&gt;&lt;/i&gt; {{ button_map }}&lt;/a&gt;\n              {% endif %} --&gt;\n            &lt;/div&gt;\n            &lt;div class=&quot;col-sm-3&quot;&gt;&lt;strong&gt;{{ text_telephone }}&lt;/strong&gt;&lt;br&gt;\n              {{ telephone }}&lt;br /&gt;\n              &lt;br /&gt;\n              {% if fax %}\n              &lt;strong&gt;{{ text_fax }}&lt;/strong&gt;&lt;br&gt;\n              {{ fax }}\n              {% endif %}\n            &lt;/div&gt;\n            &lt;div class=&quot;col-sm-3&quot;&gt;\n              {% if open %}\n              &lt;strong&gt;{{ text_open }}&lt;/strong&gt;&lt;br /&gt;\n              {{ open }}&lt;br /&gt;\n              &lt;br /&gt;\n              {% endif %}\n              {% if comment %}\n              &lt;strong&gt;{{ text_comment }}&lt;/strong&gt;&lt;br /&gt;\n              {{ comment }}\n              {% endif %}\n            &lt;/div&gt;\n          &lt;/div&gt;\n        &lt;/div&gt;\n      &lt;/div&gt;\n      {% if locations %}\n      &lt;h3&gt;{{ text_store }}&lt;/h3&gt;\n      &lt;div class=&quot;panel-group&quot; id=&quot;accordion&quot;&gt;\n        {% for location in locations %}\n        &lt;div class=&quot;panel panel-default&quot;&gt;\n          &lt;div class=&quot;panel-heading&quot;&gt;\n            &lt;h4 class=&quot;panel-title&quot;&gt;&lt;a href=&quot;#collapse-location{{ location.location_id }}&quot; class=&quot;accordion-toggle&quot; data-toggle=&quot;collapse&quot; data-parent=&quot;#accordion&quot;&gt;{{ location.name }} &lt;i class=&quot;fa fa-caret-down&quot;&gt;&lt;/i&gt;&lt;/a&gt;&lt;/h4&gt;\n          &lt;/div&gt;\n          &lt;div class=&quot;panel-collapse collapse&quot; id=&quot;collapse-location{{ location.location_id }}&quot;&gt;\n            &lt;div class=&quot;panel-body&quot;&gt;\n              &lt;div class=&quot;row&quot;&gt;\n                {% if location.image %}\n                &lt;div class=&quot;col-sm-3&quot;&gt;&lt;img src=&quot;{{ location.image }}&quot; alt=&quot;{{ location.name }}&quot; title=&quot;{{ location.name }}&quot; class=&quot;img-thumbnail&quot; /&gt;&lt;/div&gt;\n                {% endif %}\n                &lt;div class=&quot;col-sm-3&quot;&gt;&lt;strong&gt;{{ location.name }}&lt;/strong&gt;&lt;br /&gt;\n                  &lt;address&gt;\n                  {{ location.address }}\n                  &lt;/address&gt;\n                  &lt;!-- {% if location.geocode %}\n                  &lt;a href=&quot;https://maps.google.com/maps?q={{ location.geocode|url_encode }}&amp;hl={{ geocode_hl }}&amp;t=m&amp;z=15&quot; target=&quot;_blank&quot; class=&quot;btn btn-info&quot;&gt;&lt;i class=&quot;fa fa-map-marker&quot;&gt;&lt;/i&gt; {{ button_map }}&lt;/a&gt;\n                  {% endif %} --&gt;\n                &lt;/div&gt;\n                &lt;div class=&quot;col-sm-3&quot;&gt; &lt;strong&gt;{{ text_telephone }}&lt;/strong&gt;&lt;br&gt;\n                  {{ location.telephone }}&lt;br /&gt;\n                  &lt;br /&gt;\n                  {% if location.fax %}\n                  &lt;strong&gt;{{ text_fax }}&lt;/strong&gt;&lt;br&gt;\n                  {{ location.fax }}\n                  {% endif %}\n                &lt;/div&gt;\n                &lt;div class=&quot;col-sm-3&quot;&gt;\n                  {% if location.open %}\n                  &lt;strong&gt;{{ text_open }}&lt;/strong&gt;&lt;br /&gt;\n                  {{ location.open }}&lt;br /&gt;\n                  &lt;br /&gt;\n                  {% endif %}\n                  {% if location.comment %}\n                  &lt;strong&gt;{{ text_comment }}&lt;/strong&gt;&lt;br /&gt;\n                  {{ location.comment }}\n                  {% endif %}\n                &lt;/div&gt;\n              &lt;/div&gt;\n            &lt;/div&gt;\n          &lt;/div&gt;\n        &lt;/div&gt;\n        {% endfor %}\n      &lt;/div&gt;\n      {% endif %}\n      &lt;form action=&quot;{{ action }}&quot; method=&quot;post&quot; enctype=&quot;multipart/form-data&quot; class=&quot;form-horizontal&quot;&gt;\n        &lt;fieldset&gt;\n          &lt;legend&gt;{{ text_contact }}&lt;/legend&gt;\n          &lt;div class=&quot;form-group required&quot;&gt;\n            &lt;label class=&quot;col-sm-2 control-label&quot; for=&quot;input-name&quot;&gt;{{ entry_name }}&lt;/label&gt;\n            &lt;div class=&quot;col-sm-10&quot;&gt;\n              &lt;input type=&quot;text&quot; name=&quot;name&quot; value=&quot;{{ name }}&quot; id=&quot;input-name&quot; class=&quot;form-control&quot; /&gt;\n              {% if error_name %}\n              &lt;div class=&quot;text-danger&quot;&gt;{{ error_name }}&lt;/div&gt;\n              {% endif %}\n            &lt;/div&gt;\n          &lt;/div&gt;\n          &lt;div class=&quot;form-group required&quot;&gt;\n            &lt;label class=&quot;col-sm-2 control-label&quot; for=&quot;input-email&quot;&gt;{{ entry_email }}&lt;/label&gt;\n            &lt;div class=&quot;col-sm-10&quot;&gt;\n              &lt;input type=&quot;text&quot; name=&quot;email&quot; value=&quot;{{ email }}&quot; id=&quot;input-email&quot; class=&quot;form-control&quot; /&gt;\n              {% if error_email %}\n              &lt;div class=&quot;text-danger&quot;&gt;{{ error_email }}&lt;/div&gt;\n              {% endif %}\n            &lt;/div&gt;\n          &lt;/div&gt;\n          &lt;div class=&quot;form-group required&quot;&gt;\n            &lt;label class=&quot;col-sm-2 control-label&quot; for=&quot;input-enquiry&quot;&gt;{{ entry_enquiry }}&lt;/label&gt;\n            &lt;div class=&quot;col-sm-10&quot;&gt;\n              &lt;textarea name=&quot;enquiry&quot; rows=&quot;10&quot; id=&quot;input-enquiry&quot; class=&quot;form-control&quot;&gt;{{ enquiry }}&lt;/textarea&gt;\n              {% if error_enquiry %}\n              &lt;div class=&quot;text-danger&quot;&gt;{{ error_enquiry }}&lt;/div&gt;\n              {% endif %}\n            &lt;/div&gt;\n          &lt;/div&gt;\n          {{ captcha }}\n        &lt;/fieldset&gt;\n        &lt;div class=&quot;buttons&quot;&gt;\n          &lt;div class=&quot;pull-right&quot;&gt;\n            &lt;input class=&quot;btn btn-primary&quot; type=&quot;submit&quot; value=&quot;{{ button_submit }}&quot; /&gt;\n          &lt;/div&gt;\n        &lt;/div&gt;\n      &lt;/form&gt;\n      {{ content_bottom }}&lt;/div&gt;\n    {{ column_right }}\n\n    &lt;div class=&quot;col-sm-12 col-md-4&quot;&gt;\n        {% if geocode %}\n            &lt;iframe class=&quot;contact-us-iframe&quot;\n                src=&quot;https://maps.google.com/maps?q={{ geocode|url_encode }}&amp;hl={{ geocode_hl }}&amp;t=m&amp;z=15&amp;output=embed&quot; style=&quot;border:0;&quot; allowfullscreen=&quot;&quot; loading=&quot;lazy&quot; referrerpolicy=&quot;no-referrer-when-downgrade&quot;&gt;\n            &lt;/iframe&gt;\n        {% endif %}\n    &lt;/div&gt;\n    \n\n    &lt;/div&gt;\n&lt;/div&gt;\n{{ footer }}\n', '2023-09-18 12:51:22'),
	(42, 0, 'tt_rozer1', 'plaza/module/ptproducts/content', '{% if layout_type == &quot;grid&quot; %}\n    {# Grid content #}\n	&lt;div class=&quot;grid-style&quot;&gt;\n		&lt;div class=&quot;product-item&quot;&gt;	\n			&lt;div class=&quot;image {% if rotate_image %}rotate-image-container{% endif %}&quot;&gt;\n				\n				&lt;a href=&quot;{{ href }}&quot;&gt;\n					{% if rotate_image %}\n						&lt;img {% if lazy_load %} data-src=&quot;{{ rotate_image }}&quot; width=&quot;{{ img_width }}&quot; height=&quot;{{ img_height }}&quot; {% else %} src=&quot;{{ rotate_image }}&quot; {% endif %}  alt=&quot;{{ name[:20] ~ \'...\' }}&quot; title=&quot;{{ name }}&quot; class=&quot;img-responsive img-rotate-image {% if lazy_load %} swiper-lazy {% endif %}&quot; /&gt;\n					{% endif %}\n					&lt;img {% if lazy_load %} data-src=&quot;{{ thumb }}&quot; width=&quot;{{ img_width }}&quot; height=&quot;{{ img_height }}&quot; {% else %} src=&quot;{{ thumb }}&quot; {% endif %} alt=&quot;{{ name[:20] ~ \'...\' }}&quot; title=&quot;{{ name }}&quot; class=&quot;{% if lazy_load %} swiper-lazy {% endif %} img-responsive img-mod-{{ section_id }}-{{ product_id }}&quot; /&gt;\n					{% if swatches_images %}\n						&lt;input type=&quot;hidden&quot; value=&quot;{{ thumb }}&quot; class=&quot;img-default-{{ section_id }}-{{ product_id }}&quot; /&gt;\n						{% for s_image in swatches_images %}\n							&lt;input type=&quot;hidden&quot; value=&quot;{{ s_image.image }}&quot; class=&quot;img-swatch-{{ section_id }}-{{ product_id }}-{{ s_image.product_option_value_id }}&quot; /&gt;\n						{% endfor %}\n					{% endif %}\n				&lt;/a&gt;\n				{% if show_label and (special or is_new) %}\n					&lt;div class=&quot;box-label&quot;&gt;\n						{% if is_new %}\n							&lt;div&gt;&lt;span class=&quot;pro-label new&quot;&gt;{{ text_new }}&lt;/span&gt;&lt;/div&gt;\n						{% endif %}\n					&lt;/div&gt;\n				{% endif %}\n				{% if lazy_load %}\n				&lt;div class=&quot;swiper-lazy-preloader&quot;&gt;&lt;/div&gt;\n				{% endif %}\n				{% if options %}\n					&lt;div class=&quot;category-options product-options&quot;&gt;\n					{% for option in options %}\n						&lt;ul class=&quot;ul-swatches-colors&quot;&gt;\n							{% for option_value in option.product_option_value %}\n								&lt;li class=&quot;swatches-options&quot;&gt;\n									&lt;a href=&quot;javascript:void(0);&quot; onclick=&quot;swatches.changeModuleSwatches($(this));&quot; title=&quot;{{ option_value.name }}&quot;\n									   style=&quot;{% if option_value.image %} width: 10px; height: 10px;\n																			background-image: url(\'{{ option_value.image }}\') {% endif %}&quot;\n									   data-module-id=&quot;{{ section_id }}&quot;\n									   data-product-id=&quot;{{ product_id }}&quot;\n									   data-product-option-value-id=&quot;{{ option_value.product_option_value_id }}&quot;&gt;{% if not option_value.image %} {{ option_value.name }} {% endif %}&lt;/a&gt;\n								&lt;/li&gt;\n							{% endfor %}\n						&lt;/ul&gt;\n					{% endfor %}\n					&lt;/div&gt;\n				{% endif %}\n				{% if show_quickview %}\n					&lt;button type=&quot;button&quot; title=&quot;{{ button_quickview }}&quot; class=&quot;button-quickview&quot; onclick=&quot;ptquickview.ajaxView(\'{{ href }}\')&quot;&gt;&lt;span&gt;{{ button_quickview }}&lt;/span&gt;&lt;/button&gt;\n				{% endif %}\n			&lt;/div&gt;\n			&lt;div class=&quot;caption&quot;&gt;\n				&lt;div class=&quot;inner&quot;&gt;\n					{% if manufacturer %}\n						&lt;p class=&quot;manufacture-product&quot;&gt;\n							&lt;a href=&quot;{{ manufacturer_href }}&quot;&gt;{{ manufacturer }}&lt;/a&gt;\n						&lt;/p&gt;\n					{% endif %}\n					&lt;h4&gt;&lt;a href=&quot;{{ href }}&quot;&gt;{{ name }}&lt;/a&gt;&lt;/h4&gt;\n					{% if rating %}\n						&lt;div class=&quot;rating&quot;&gt;\n							{% for i in 1..5 %}\n								{% if rating &lt; i %}\n									&lt;span class=&quot;icon-ratings&quot;&gt;&lt;i class=&quot;icon-rating icon-rating-o&quot;&gt;&lt;/i&gt;&lt;/span&gt;\n								{% else %}\n									&lt;s